import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Map, MapMarker, CustomOverlayMap } from "react-kakao-maps-sdk";

function Gas_station() {
    const [state, setState] = useState({
        center: { lat: 33.450701, lng: 126.570667 },
        errMsg: null,
        isLoading: true,
    });

    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const newPos = { lat: position.coords.latitude, lng: position.coords.longitude };
                    setState(prev => ({ ...prev, center: newPos, isLoading: false }));
                    fetchGasStationInfo(newPos.lat, newPos.lng);
                },
                (err) => {
                    setState(prev => ({ ...prev, errMsg: err.message, isLoading: false }));
                }
            );
        } else {
            setState(prev => ({ ...prev, errMsg: "Geolocation을 사용할 수 없어요.", isLoading: false }));
        }
    }, []);

    const fetchGasStationInfo = (latitude, longitude) => {
        axios.post('http://localhost:5000/fetch-gas-station', { latitude, longitude })
            .then(response => {
                console.log('Gas stations fetched:', response.data);
            })
            .catch(error => {
                setState(prev => ({ ...prev, errMsg: '주유소 정보를 가져오는데 실패했습니다.', isLoading: false }));
            });
    };

    return (
        <Map center={state.center} style={{ width: "100%", height: "100%" }} level={3}>
            {!state.isLoading && (
                <MapMarker position={state.center} />
            )}
        </Map>
    );
}



export default Gas_station;
