import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Map, CustomOverlay } from "react-kakao-maps-sdk";


function Modal({ isOpen, onClose, children }) {
    if (!isOpen) return null;

    return (
        <div style={{
            position: 'fixed',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            backgroundColor: 'white',
            padding: '20px',
            zIndex: 1000,
            borderRadius: '8px',
            boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
            width: '300px'
        }}>
            <button onClick={onClose} style={{
                position: 'absolute',
                right: '10px',
                top: '10px',
                border: 'none',
                background: 'transparent',
                fontSize: '24px',
                cursor: 'pointer',
                color: '#333'  // 닫기 버튼의 색상 조정
            }}>
                &times;  // HTML 엔티티를 사용하여 "×" 표시
            </button>
            {children}
        </div>
    );
}


