import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Map, MapMarker, MapInfoWindow } from "react-kakao-maps-sdk";



function Popup({ station, onClose }) {
    if (!station) return null; // 선택된 주유소가 없다면 팝업을 렌더링하지 않음

    // 팝업창 위치와 크기 설정
    const popupStyle = {
        position: 'absolute',
        bottom: '150px', // 마커 위치에 따라 조절 필요
        left: '50%',
        transform: 'translateX(-50%)',
        width: '200px',
        padding: '15px',
        border: '1px solid #ccc',
        borderRadius: '5px',
        backgroundColor: '#fff',
        fontSize: '12px',
        textAlign: 'center',
        zIndex: 100,
    };

    return (
        <div style={popupStyle}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
                <span style={{ fontWeight: 'bold' }}>{station.name}</span>
                <img
                    src={`path/to/brand-icons/${station.brand}.png`}
                    alt={station.brand}
                    style={{ width: '24px', height: '24px' }}
                />
            </div>
            <div style={{ margin: '10px 0' }}>
                <p><strong>가격:</strong> {station.price}원</p>
                <p><strong>거리:</strong> {station.distance}m</p>
            </div>
            <button onClick={onClose} style={{
                cursor: 'pointer',
                backgroundColor: '#f00',
                color: '#fff',
                border: 'none',
                borderRadius: '5px',
                padding: '5px 10px',
                margin: '5px 0'
            }}>
                닫기
            </button>
        </div>
    );
}

function GasStation() {
    const [state, setState] = useState({
        center: { lat: 33.450701, lng: 126.570667 },
        stations: [],
        errMsg: null,
        isLoading: true,
        selectedStation: null,
    });

    useEffect(() => {
        const fetchStations = (position) => {
            const { latitude, longitude } = position.coords;
            axios.post('http://localhost:5000/get-stations', { latitude, longitude })
                .then(response => {
                    setState(prev => ({
                        ...prev,
                        center: { lat: latitude, lng: longitude },
                        stations: response.data,
                        isLoading: false
                    }));
                })
                .catch(error => {
                    setState(prev => ({
                        ...prev,
                        errMsg: '주유소 정보를 가져오는데 실패했습니다.',
                        isLoading: false
                    }));
                });
        };

        navigator.geolocation.getCurrentPosition(fetchStations, (err) => {
            setState(prev => ({ ...prev, errMsg: err.message, isLoading: false }));
        });
    }, []);

    const onMarkerClick = (station) => {
        setState(prev => ({ ...prev, selectedStation: station }));
    };

    const closeInfoWindow = () => {
        setState(prev => ({ ...prev, selectedStation: null }));
    };


    return (
        <Map center={state.center} style={{ width: "100%", height: "100%" }} level={3}>
            <MapMarker position={state.center} />
            {!state.isLoading && state.stations.map(station => (
                <MapMarker
                    key={station.name}
                    position={{ lat: station.latitude, lng: station.longitude }}
                    image={{
                        src: "img/fuel.png", // Replace with actual marker image path
                        size: { width: 24, height: 35 },
                    }}
                    onClick={() => onMarkerClick(station)}
                >
                    {state.selectedStation?.name === station.name && (
                        <MapInfoWindow
                            position={{ lat: station.latitude, lng: station.longitude }}
                            removable // 이 옵션을 추가하여 정보창에 기본 제공되는 닫기 버튼을 활성화할 수 있습니다.
                        >
                            <div style={{
                                padding: '15px',
                                border: '1px solid #ccc',
                                borderRadius: '5px',
                                backgroundColor: '#fff',
                                width: '200px',
                                fontSize: '12px',
                                textAlign: 'center',
                            }}>
                                <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px' }}>
                                    <span style={{ fontWeight: 'bold' }}>{station.name}</span>
                                    <img
                                        src={`path/to/brand-icons/${station.brand}.png`}
                                        alt={station.brand}
                                        style={{ width: '24px', height: '24px' }}
                                    />
                                </div>
                                <div style={{ margin: '10px 0' }}>
                                    <p><strong>가격:</strong> {station.price}원</p>
                                    <p><strong>거리:</strong> {station.distance}m</p>
                                </div>
                                <button onClick={closeInfoWindow} style={{
                                    cursor: 'pointer',
                                    backgroundColor: '#f00',
                                    color: '#fff',
                                    border: 'none',
                                    borderRadius: '5px',
                                    padding: '5px 10px',
                                    margin: '5px 0'
                                }}>
                                    닫기
                                </button>
                            </div>
                        </MapInfoWindow>

                    )}
                </MapMarker>
            ))}
        </Map>

    );
}

export default GasStation;
