import './App.css';
import {useState} from "react";


function App() {


    [1, 2, 3].map(function (a) {
        return '1233211'
    })

    let post = '강남 우동 맛집';
    let [title, settitle] = useState(['남자 코트 추천', '강남 우동맛집', '파이썬독학'])
    let [like, setlike] = useState([0, 0, 0]);
    let [modal, setModal] = useState(false);

    return (
        <div className="App">
            <div className='black-nav'>
                <h4>블로그임 </h4>
            </div>

            <button onClick={() => {
                let copy = [...title];
                copy[0] = '여자코트 추천';
                settitle(copy)
            }}> 수정버튼
            </button>


            {
                title.map(function (a, i) {
                    return (
                        <div className="list" key={i}>
                            <h4 onClick={()=>{setModal(!modal)}} >{ title[i] }
                                <span onClick={() => {
                                    let copy = [...like];
                                    copy[i] = copy[i] + 1;
                                    setlike(copy)
                                }}>👍</span>{like[i]}</h4>
                            <p>2월 17일 발행</p>
                        </div>


                    )
                })
            }

            {
                // modal == true ? <Modal 작명={state이름}/> : null 보통 작명은 state이름이랑 똑같은 이름으로 함.
                modal == true ? <Modal color={'skyblue'} title={title}/> : null
            }

        </div>

    );
}


function Modal(props) {                                {/*모달창안에 첫째글제목을 {title[0] 이렇게하면 쓸수가없음.문법이 틀린게아니라 다른 함수에있는걸 modal함수에서 사용하려고 하기때문에 사용이 안되는것
                                                      props 문법을 사용하면 해결가능 부모 컴포넌트(App)에서 자식 컴포넌트(Modal)로 전송 가능
                                                      props를 쓰고싶으면 두가지 스텝이 있음.
                                                      1.<자식컴포넌트 작명={state이름}>
                                                      2.props 파라미터 등록 후 props.작명 사용

                                                      부모에서 자식으로만 전송 가능하다.
                                                      그래서 컴포넌트가 많아지면 props 쓰는게 귀찮아짐.





                                                                                                       }*/}
    return (
        <div className="modal" style={{background : props.color}}>
            <h4>{props.title[0]}</h4>
            <p>날짜</p>
            <p>상세내용</p>
        </div>
    )
}


export default App;
