import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Map, MapMarker, CustomOverlayMap } from "react-kakao-maps-sdk";

function GasStation() {
    const [state, setState] = useState({
        center: { lat: 33.450701, lng: 126.570667 },
        stations: [],
        errMsg: null,
        isLoading: true,
        selectedStation: null,
    });

    useEffect(() => {
        const fetchStations = (position) => {
            const { latitude, longitude } = position.coords;
            axios.post('http://localhost:5000/get-stations', { latitude, longitude })
                .then(response => {
                    setState(prev => ({
                        ...prev,
                        center: { lat: latitude, lng: longitude },
                        stations: response.data,
                        isLoading: false
                    }));
                })
                .catch(error => {
                    setState(prev => ({
                        ...prev,
                        errMsg: '주유소 정보를 가져오는데 실패했습니다.',
                        isLoading: false
                    }));
                });
        };

        navigator.geolocation.getCurrentPosition(fetchStations, (err) => {
            setState(prev => ({ ...prev, errMsg: err.message, isLoading: false }));
        });
    }, []);

    const onMarkerClick = (station) => {
        setState(prev => ({ ...prev, selectedStation: station }));
    };

    const closeInfoWindow = () => {
        setState(prev => ({ ...prev, selectedStation: null }));
    };

    return (
        <Map center={state.center} style={{ width: "100%", height: "100%" }} level={3}>
            {/* ... 사용자 위치 마커 ... */}
            {!state.isLoading && state.stations.map(station => (
                <MapMarker
                    // ... 기존 MapMarker 속성들 ...
                    onClick={() => onMarkerClick(station)}
                >
                    {state.selectedStation && state.selectedStation.name === station.name && (
                        <CustomOverlayMap position={{ lat: station.latitude, lng: station.longitude }}>
                            <div style={{
                                padding: "15px",
                                background: "rgba(255, 255, 255, 0.9)",
                                border: "1px solid #ddd",
                                boxShadow: "0 2px 6px rgba(0, 0, 0, 0.3)",
                                borderRadius: "10px",
                                fontSize: "13px",
                                maxWidth: "300px",
                                lineHeight: "1.4",
                                fontFamily: "'Noto Sans KR', sans-serif"
                            }}>
                                <button onClick={closeInfoWindow} style={{
                                    position: "absolute",
                                    top: "10px",
                                    right: "10px",
                                    cursor: "pointer",
                                    border: "none",
                                    background: "#fff",
                                    borderRadius: "50%",
                                    width: "24px",
                                    height: "24px",
                                    lineHeight: "24px",
                                    textAlign: "center",
                                    boxShadow: "0 1px 3px rgba(0, 0, 0, 0.3)",
                                    fontSize: "16px",
                                    fontWeight: "bold",
                                    color: "#333"
                                }}>&times;</button>
                                <strong>{station.name}</strong><br />
                                가격: {station.price}원<br />
                                거리: {station.distance}m<br />
                                {/* ... 추가 정보가 있다면 여기에 ... */}
                            </div>
                        </CustomOverlayMap>
                    )}
                </MapMarker>
            ))}
        </Map>
    );
}

export default GasStation;
