import React, { useState } from 'react';
import styles from './Header.module.css';
import { NavLink, useLocation } from 'react-router-dom';

function Header(props) {
    const [isMenuOpen, setMenuOpen] = useState(false);
    const location = useLocation(); // ÌòÑÏû¨ ÏúÑÏπò Ï†ïÎ≥¥Î•º Í∞ÄÏ†∏ÏòµÎãàÎã§.
    const isRestArea = location.pathname === '/restArea'; // /restArea Í≤ΩÎ°úÏù∏ÏßÄ ÌôïÏù∏Ìï©ÎãàÎã§.
    const isJeju = location.pathname === '/jeju'; // /jeju Í≤ΩÎ°úÏù∏ÏßÄ ÌôïÏù∏Ìï©ÎãàÎã§.
    // /restAreaÏùº Îïå ÏÇ¨Ïö©Ìï† Ïä§ÌÉÄÏùº
    const headerClass = isRestArea || isJeju ? `${styles.nav_wrap} ${styles.specialAreaActive}` : styles.nav_wrap;

    return (
        <div className={headerClass}>
            <nav className={styles.nav}>
                <div className={styles.logo}><NavLink to='/'>
                    {/*<span>STOP</span>SCAN*/}
                    <img src={"../img/logo1.png"} style={{width : '250px'}}/>
                </NavLink></div>
                <button className={styles.menu_button} onClick={() => setMenuOpen(!isMenuOpen)}>
                    &#9776;
                    {/*Ïù¥Í≤å ÌñÑÎ≤ÑÍ±∞ Î™®Ïñë Ïú†ÎãàÏΩîÎìúÎ¨∏Ïûê*/}
                </button>
                <div className={`${styles.menu_box} ${isMenuOpen ? styles.menu_open :styles.menu_closed}`}>
                    <ul>
                        <li><NavLink to="/map">Ï£ºÏú†ÏÜåÏ∞æÍ∏∞</NavLink></li>
                        <li><NavLink to="/restArea">Ìú¥Í≤åÏÜå Ï∞æÍ∏∞</NavLink></li>
                        <li><NavLink to="/board">Í≤åÏãúÌåê</NavLink></li>
                        <li><NavLink to="/jeju">Ï†úÏ£º Ïñ¥ÎïåüèùÔ∏è</NavLink></li>
                        <li><NavLink to="/sub">Ïú†Í∞ÄÌÜµÍ≥Ñ</NavLink></li>
                    </ul>
                </div>
                <div className={styles.login_box}>
                    {props.isLoggedIn ? <div>Î∞òÍ∞ëÏäµÎãàÎã§.{props.userRealName}</div> :
                        <NavLink to="/login">
                            <span>LOGIN</span>
                        </NavLink>
                    }

                </div>
            </nav>
        </div>
    );
}

export default Header;
