/* eslint-disable */
import './App.css';
import {useState} from "react";


function App() {


    let post = '강남 우동 맛집';
    let [title, settitle] = useState(['남자 코트 추천', '강남 우동맛집', '파이썬독학'])
    let [like, setlike] = useState(0);
    let [modal, setModal] = useState(false); /*UI의 현재 상태를 state로 저장하는 부분 닫히거나 열리거나 둘중에 하나이기때문에 숫자로 0이면 닫힘 1이면 열림 false면 닫힘 True면 열림 이런식으로 자유롭게 자료형 선정*/

    return (
        <div className="App">
            <div className='black-nav'>
                <h4>블로그임 </h4>
            </div>

            <button onClick={() => {
                let copy = [...title];
                copy[0] = '여자코트 추천';
                settitle(copy)
            }}> 수정버튼
            </button>


            <div className="list">
                <h4>{title[0]} <span onClick={() => {
                    setlike(like + 1)
                }}>👍</span>{like}</h4>
                <p>2월 17일 발행</p>
            </div>
            <div className="list">
                <h4>{title[1]}</h4>
                <p>2월 17일 발행</p>
            </div>
            <div className="list">
                <h4 onClick={()=>{
                    let copy = [...title]
                    copy = true
                    settitle(copy)

                }}>{title[2]}</h4>
                <p>2월 17일 발행</p>
            </div>

            {/*{*/}
            {/*    조건식 ? 참일때 실행할코드 : 거짓일때 실행할 코드*/}
            {/*}*/}

            {
                modal == true ? <Modal></Modal> : null
            }

            {/*<Modal></Modal> /!*state가 false면 안보이게 하려면 조건문으로 안보이게 해줘야함. 조건문을 쓰려면 중괄호를 펼쳐주고 if를 사용하면 좋겠지만 사용이안된다.그래서 삼항연산자로 조건문을 만들어주면된다.*!/*/}

        {/*    동적인 UI 만드는 STEP
        1.html css로 미리 디자인완성
        2.UI의 현재 상태를 state로 저장
        3.state에 따라 UI가 어떻게 보일지 작성
        */}


        </div>

    );
}


function Modal() {
    return (
        <div className="modal">
            <h4>제목</h4>
            <p>날짜</p>
            <p>상세내용</p>
        </div>
    )
}


export default App;
