import { Map, MapMarker, CustomOverlay } from "react-kakao-maps-sdk";
import { useEffect, useState } from "react";
import axios from 'axios';

function My_Location() {
    const [state, setState] = useState({
        center: {
            lat: 33.450701,
            lng: 126.570667,
        },
        locations: [],  // 충전소 위치 데이터
        errMsg: null,
        isLoading: true,
    });

    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const newPos = {
                        lat: position.coords.latitude,
                        lng: position.coords.longitude,
                    };
                    setState(prev => ({
                        ...prev,
                        center: newPos,
                        isLoading: false,
                    }));
                    fetchStations(newPos.lat, newPos.lng);  // 위치 기반으로 주변 충전소 정보 요청
                },
                (err) => {
                    setState(prev => ({
                        ...prev,
                        errMsg: err.message,
                        isLoading: false,
                    }));
                }
            );
        } else {
            setState(prev => ({
                ...prev,
                errMsg: "Geolocation을 사용할 수 없어요..",
                isLoading: false,
            }));
        }
    }, []);

    const fetchStations = (latitude, longitude) => {
        axios.post('http://localhost:5000/find-stations', { latitude, longitude })
            .then((response) => {
                setState(prev => ({
                    ...prev,
                    locations: response.data,  // 서버로부터 받은 충전소 데이터 저장
                }));
            })
            .catch((error) => {
                console.error('Error fetching stations:', error);
                setState(prev => ({
                    ...prev,
                    errMsg: '충전소 정보를 가져오는데 실패했습니다.',
                    isLoading: false,
                }));
            });
    };

    return (
        <>
            <Map
                center={state.center}
                style={{ width: "100%", height: "400px" }}
                level={3} // 지도의 확대 레벨
            >
                {!state.isLoading && (
                    <MapMarker position={state.center}>
                        <div style={{ padding: "5px", color: "#000" }}>
                            {state.errMsg ? state.errMsg : "현재 위치"}
                        </div>
                    </MapMarker>
                )}
                {state.locations.map((loc, index) => (
                    <React.Fragment key={index}>
                        <MapMarker
                            position={{ lat: loc.lat, lng: loc.lng }}
                            image={{
                                src: "/images/eleccharger.png", // 마커 이미지
                                size: { width: 50, height: 30 },
                            }}
                        >
                            <CustomOverlay position={{ lat: loc.lat, lng: loc.lng }}>
                                <div style={{ padding: "5px", background: "#fff", border: "1px solid black", borderRadius: "4px", fontSize: "12px" }}>
                                    <strong>{loc.statNm}</strong><br />
                                    {loc.addr}<br />
                                    Distance: {loc.distance.toFixed(2)} km
                                </div>
                            </CustomOverlay>
                        </MapMarker>
                    </React.Fragment>
                ))}
            </Map>
        </>
    );
}

export default My_Location;
