/* eslint-disable */
// warning 메세지 없애고 싶을때 위에 코드를 적으면됨.
import './App.css';
import {useState} from "react";




function App() {


    let post ='강남 우동 맛집';
    let [title, settitle]= useState(['남자 코트 추천','강남 우동맛집','파이썬독학'])
    let [like, setlike] = useState(0);

    return (
        <div className="App">
            <div className='black-nav'>
                <h4>블로그임 </h4>
            </div>

            <button onClick={() => {
                let copy = [...title];
                copy[0] = '여자코트 추천';
                settitle(copy)
            }}> 수정버튼
            </button>


            <div className="list">
                <h4>{title[0]} <span onClick={() => {
                    setlike(like + 1)
                }}>👍</span>{like}</h4>
                <p>2월 17일 발행</p>
            </div>
            <div className="list">
                <h4>{title[1]}</h4>
                <p>2월 17일 발행</p>
            </div>
            <div className="list">
                <h4>{title[2]}</h4>
                <p>2월 17일 발행</p>
            </div>

            <Modal></Modal> {/*첨보는 사람도 이해하기쉬움, 미래의 나도 이해하기쉬움 */}
            {/*

            컴포넌트 만드는 3스텝
            1. function 만들고
            2. return()안에 html 담기
            3. <함수명></함수명> 쓰기


                     */}

            {/*<div className="modal">  /!*이렇게 모달을 만들어봤는데 이런html에 특징:더러움 , 무슨 역할인지 해석하기도 어려움 그래서 리액트에서는 더러운 html덩어리들을 한단어로 축약할수있는*/}
            {/* component 문법이라는게 있음.*!/*/}
            {/*    <h4>제목</h4>*/}
            {/*    <p>날짜</p>*/}
            {/*    <p>상세내용</p>*/}
            {/*</div>*/}

        </div>

    );
}

// function 작명(){ 이렇게 다른 함수 바깥에다 만들어야함. function App라는 함수안에다가 만드는게 아니라 이렇게 바깥에다 만들어야함.작명할때 관습적으로 첫글자는 대문자사용
//
// }

    function Modal(){
    return (
        <div className="modal">
            <h4>제목</h4>
            <p>날짜</p>
            <p>상세내용</p>
        </div>
    )
    }
//     어떤걸 컴포넌트로 만들면 좋은가
// 1. 반복적인 html 축약할때
// 2. 큰 페이지들
// 3. 자주변경되는 것들
//  컴포넌트의 단점: state 가져다 쓸때 문제가생김 다른 함수에있는 state를 사용하려고하기때문에 function App에있던 state를 function Modal 에서 쓸수없음.

export default App;
