import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Map, MapMarker, CustomOverlayMap } from "react-kakao-maps-sdk";

function My_Location() {
    const [state, setState] = useState({
        center: { lat: 33.450701, lng: 126.570667 },
        locations: [],
        errMsg: null,
        isLoading: true,
        showOverlayId: null // 현재 활성화된 팝업의 ID
    });



    useEffect(() => {
        if (navigator.geolocation) {
            navigator.geolocation.getCurrentPosition(
                (position) => {
                    const newPos = { lat: position.coords.latitude, lng: position.coords.longitude };
                    setState(prev => ({ ...prev, center: newPos, isLoading: false }));
                    fetchStations(newPos.lat, newPos.lng);
                },
                (err) => {
                    setState(prev => ({ ...prev, errMsg: err.message, isLoading: false }));
                }
            );
        } else {
            setState(prev => ({ ...prev, errMsg: "Geolocation을 사용할 수 없어요.", isLoading: false }));
        }
    }, []);

    const fetchStations = (latitude, longitude) => {
        axios.post('http://localhost:5000/find-stations', { latitude, longitude })
            .then(response => {
                setState(prev => ({ ...prev, locations: response.data.dbData, isLoading: false }));
            })
            .catch(error => {
                console.error('Error fetching stations:', error);
                setState(prev => ({ ...prev, errMsg: '충전소 정보를 가져오는데 실패했습니다.', isLoading: false }));
            });
    };

    const getMarkerImage = (stat) => {
        switch (stat) {
            case '2': return '/img/green_marker.png'; // 충전 대기
            case '3': return '/img/orange_marker.png'; // 충전 중
            default: return '/img/default_elec.png'; // 기타 상태
        }
    };

    const toggleOverlay = (statId) => {
        setState(prev => ({
            ...prev,
            showOverlayId: prev.showOverlayId === statId ? null : statId
        }));
    };

    // 스타일 정의
    const customOverlayStyle = {
        padding: "15px",
        background: "rgba(255, 255, 255, 0.9)", // 반투명 효과
        border: "1px solid #ddd", // 가벼운 테두리
        boxShadow: "0 2px 6px rgba(0, 0, 0, 0.3)", // 그림자 효과
        borderRadius: "10px", // 둥근 모서리
        fontSize: "13px",
        maxWidth: "300px", // 팝업창의 최대 너비 설정
        lineHeight: "1.4", // 줄 간격
        fontFamily: "'Noto Sans KR', sans-serif", // 폰트 변경
    };

    const closeButtonStyle = {
        position: "absolute",
        top: "10px",
        right: "10px",
        cursor: "pointer",
        border: "none",
        background: "#fff",
        borderRadius: "50%", // 원형 버튼
        width: "24px", // 버튼 크기
        height: "24px",
        lineHeight: "24px", // 텍스트 라인 높이를 버튼 높이와 맞춤
        textAlign: "center", // 텍스트 중앙 정렬
        boxShadow: "0 1px 3px rgba(0, 0, 0, 0.3)", // 버튼 그림자 효과
        fontSize: "16px",
        fontWeight: "bold", // 굵은 폰트
        color: "#333", // 버튼 X 색상
    };

    const getChargerStatusMessage = (stat) => {
        switch (stat) {
            case '1':
                return '통신 이상';
            case '2':
                return '충전 대기';
            case '3':
                return '충전 중';
            case '4':
                return '운영 중지';
            case '5':
                return '점검 중';
            case '9':
                return '상태 미확인';
            default:
                return '정보 없음';
        }
    };

    return (
        <>
            <Map center={state.center} style={{ width: "100%", height: "100%" }} level={3}>
                {!state.isLoading && (
                    <MapMarker position={state.center}>
                        <div style={{ padding: "5px", color: "#000" }}>
                            {state.errMsg ? state.errMsg : "현재 위치"}
                        </div>
                    </MapMarker>
                )}
                {state.locations.map(loc => (
                    <React.Fragment key={loc.statId}>
                        <MapMarker
                            position={{ lat: loc.lat, lng: loc.lng }}
                            image={{
                                src: getMarkerImage(loc.stat),
                                size: { width: 40, height: 40 }
                            }}
                            onClick={() => toggleOverlay(loc.statId)}
                        >
                            {state.showOverlayId === loc.statId && (
                                <CustomOverlayMap position={{ lat: loc.lat, lng: loc.lng }}>
                                    <div style={customOverlayStyle}>
                                        <button onClick={() => toggleOverlay(null)} style={closeButtonStyle}>
                                            &times; {/* X 대신 HTML 엔티티 사용 */}
                                        </button>
                                        <strong style={{ display: 'block', marginBottom: '5px' }}>{loc.statNm}</strong>
                                        <span style={{ display: 'block', color: '#555', marginBottom: '3px' }}>{loc.addr}</span>
                                        <span style={{ display: 'block', color: '#888', fontSize: '12px' }}>
            Distance: {loc.distance.toFixed(2)} km
        </span>
                                    </div>
                                </CustomOverlayMap>
                            )}
                        </MapMarker>
                    </React.Fragment>
                ))}
            </Map>
        </>
    );
}

export default My_Location;
