import React, { useState } from 'react';
import { Map, MapMarker } from "react-kakao-maps-sdk"; // Import MapMarker
import styles from './jejuMap.module.css';

function JejuMap({ spots, selectedSpot, onSelectSpot }) {
    const [visibleOverlays, setVisibleOverlays] = useState(Array(spots.length).fill(false));

    const renderOverlay = (spot) => {
        const tags = spot.TAG ? spot.TAG.split(', ').slice(0, 5) : [];
        return `<div style="padding:5px; background-color:white; border:1px solid black; border-radius:10px;">
            <h4>${spot.TITLE}</h4>
            <p>${spot.ROAD_ADDRESS}</p>
            <p>${spot.PHONE_NO}</p>
            ${tags.map(tag => `<span>${tag}</span>`).join(' ')}
            <p>${spot.INTRODUCTION}</p>
            <img src=${spot.THUMBNAIL_PATH} alt="Image" style="width:100px; height:100px;"/>
        </div>`;
    };

    const handleMouseOver = index => {
        const updatedVisibility = visibleOverlays.slice();
        updatedVisibility[index] = true;
        setVisibleOverlays(updatedVisibility);
    };

    const handleMouseOut = index => {
        const updatedVisibility = visibleOverlays.slice();
        updatedVisibility[index] = false;
        setVisibleOverlays(updatedVisibility);
    };

    return (
        <div id={styles.mainJ2}>
            <div className={styles.mapSpace}>
                <Map
                    center={{ lat: selectedSpot ? selectedSpot.LATITUDE : 33.36, lng: selectedSpot ? selectedSpot.LONGITUDE : 126.55 }}
                    level={9}
                    style={{ width: "100%", height: "100%" }}
                >
                    {spots.map((spot, index) => (
                        <MapMarker
                            key={spot.CONTENTS_ID}
                            position={{ lat: spot.LATITUDE, lng: spot.LONGITUDE }}
                            image={{
                                src: '/img/관광지.png', // 이미지 경로 설정
                                size: { width: 36, height: 36 }, // 마커 이미지 크기 조정
                            }}
                            onClick={() => onSelectSpot(spot)} // 마커 클릭 이벤트
                        />
                    ))}
                </Map>
            </div>
            <div className={styles.tourList}>
                {spots.map(spot => (
                    <div key={spot.CONTENTS_ID} className={styles.tourItem} onClick={() => onSelectSpot(spot)}>
                        <img src={spot.THUMBNAIL_PATH} alt={spot.TITLE} style={{ width: "100px", height: "100px" }} />
                        <h3>{spot.TITLE}</h3>
                        <p>{spot.ROAD_ADDRESS}</p>
                        <p>{spot.PHONE_NO}</p>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default JejuMap;
